import React, { useEffect, useState } from "react";
import {
  fetchProducts,
  addProduct,
  updateProduct,
  deleteProduct,
} from "../../api/productAPI";

const ProductTable = () => {
  const [products, setProducts] = useState([]);
  const [editingProduct, setEditingProduct] = useState(null); // sản phẩm đang sửa
  const [newProduct, setNewProduct] = useState({
    name: "",
    description: "",
    price: 0,
    category_id: 5,
    category_id_combo: 3,
    category_id_uu_dai: 1,
    stock: 0,
    url_img: "",
  });

  // Lấy dữ liệu từ server
  useEffect(() => {
    fetchProducts()
      .then((res) => setProducts(res.data))
      .catch((err) => console.error(err));
  }, []);

  // Thêm sản phẩm mới
  const handleAdd = () => {
    addProduct(newProduct)
      .then((res) => {
        setProducts([res.data, ...products]);
        setNewProduct({
          name: "",
          description: "",
          price: 0,
          category_id: 5,
          category_id_combo: 3,
          category_id_uu_dai: 1,
          stock: 0,
          url_img: "",
        });
      })
      .catch((err) => console.error(err));
  };

  // Cập nhật sản phẩm
  const handleUpdate = () => {
    updateProduct(editingProduct.product_id, editingProduct)
      .then((res) => {
        setProducts((prev) =>
          prev.map((p) =>
            p.product_id === res.data.product_id ? res.data : p
          )
        );
        setEditingProduct(null);
      })
      .catch((err) => console.error(err));
  };

  // Xoá sản phẩm
  const handleDelete = (id) => {
    deleteProduct(id)
      .then(() => {
        setProducts(products.filter((p) => p.product_id !== id));
      })
      .catch((err) => console.error(err));
  };

  return (
    <div>
      <h2 className="text-xl font-semibold mb-2">Thêm sản phẩm mới</h2>
      <input
        placeholder="Tên sản phẩm"
        value={newProduct.name}
        onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
      />
      {/* Thêm input cho các trường khác tương tự */}
      <button onClick={handleAdd}>Thêm</button>

      <h2 className="text-xl font-semibold mt-6">Danh sách sản phẩm</h2>
      <table>
        <thead>
          <tr>
            <th>Tên</th>
            <th>Giá</th>
            <th>Kho</th>
            <th>Hành động</th>
          </tr>
        </thead>
        <tbody>
          {products.map((p) => (
            <tr key={p.product_id}>
              <td>
                {editingProduct?.product_id === p.product_id ? (
                  <input
                    value={editingProduct.name}
                    onChange={(e) =>
                      setEditingProduct({ ...editingProduct, name: e.target.value })
                    }
                  />
                ) : (
                  p.name
                )}
              </td>
              <td>{p.price}</td>
              <td>{p.stock}</td>
              <td>
                {editingProduct?.product_id === p.product_id ? (
                  <button onClick={handleUpdate}>Lưu</button>
                ) : (
                  <button onClick={() => setEditingProduct(p)}>Sửa</button>
                )}
                <button onClick={() => handleDelete(p.product_id)}>Xoá</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ProductTable;
