import React, { useState, useEffect } from 'react';
import './ProductFormModel.css'; // Tùy bạn dùng CSS riêng

const ProductFormModal = ({ show, onClose, onSubmit, editingProduct }) => {
  const [formData, setFormData] = useState({
    Ten: '',
    Gia: '',
    Chiphi: '',
    Loinhuan: '',
    Loai: '',
    Mota: ''
  });

  // Nếu đang chỉnh sửa thì điền dữ liệu vào form
  useEffect(() => {
    if (editingProduct) {
      setFormData(editingProduct);
    } else {
      setFormData({
        Ten: '',
        Gia: '',
        Chiphi: '',
        Loinhuan: '',
        Loai: '',
        Mota: ''
      });
    }
  }, [editingProduct]);

  if (!show) return null;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Kiểm tra trường rỗng đơn giản
    const { Ten, Gia, Chiphi, Loinhuan, Loai, Mota } = formData;
    if (!Ten || !Gia || !Chiphi || !Loinhuan || !Loai || !Mota) {
      alert('Vui lòng điền đầy đủ thông tin.');
      return;
    }

    onSubmit(formData);
  };

  return (
    <div className="modal-backdrop">
      <div className="modal">
        <h2>{editingProduct ? 'Chỉnh sửa sản phẩm' : 'Thêm sản phẩm mới'}</h2>
        <form onSubmit={handleSubmit}>
          <label>Tên sản phẩm</label>
          <input name="Ten" value={formData.Ten} onChange={handleChange} />

          <label>Giá</label>
          <input name="Gia" value={formData.Gia} onChange={handleChange} type="number" />

          <label>Chi phí</label>
          <input name="Chiphi" value={formData.Chiphi} onChange={handleChange} type="number" />

          <label>Lợi nhuận</label>
          <input name="Loinhuan" value={formData.Loinhuan} onChange={handleChange} type="number" />

          <label>Loại</label>
          <input name="Loai" value={formData.Loai} onChange={handleChange} />

          <label>Mô tả</label>
          <textarea name="Mota" value={formData.Mota} onChange={handleChange} />

          <div className="modal-buttons">
            <button type="submit">{editingProduct ? 'Lưu thay đổi' : 'Thêm mới'}</button>
            <button type="button" onClick={onClose}>Hủy</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProductFormModal;
