import axios from 'axios';

const API_BASE = 'http://localhost:5000/api/categories';

export const getCategories = async () => {
  try {
    const response = await axios.get(API_BASE);
    return response.data;
  } catch (error) {
    console.error('Lỗi khi lấy danh mục sản phẩm:', error);
    throw error; // để component phía trên biết và xử lý
  }
};
export const createCategory = async (categoryData) => {
  const response = await axios.post(API_BASE, categoryData);
  return response.data;
};

export const updateCategory = async (id, updatedData) => {
  const response = await axios.put(`${API_BASE}/${id}`, updatedData);
  return response.data;
};

export const deleteCategory = async (id) => {
  const response = await axios.delete(`${API_BASE}/${id}`);
  return response.data;
};
