import axios from 'axios';

const API_BASE_URL = 'http://localhost:8080/api/user';

const userService = {
  // Lấy profile người dùng hiện tại
  getProfile: async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/profile`);
      return response.data;
    } catch (error) {
      console.error('Error fetching profile:', error);
      throw error;
    }
  },

  // Cập nhật thông tin người dùng
  updateProfile: async (profileData) => {
    try {
      const response = await axios.post(`${API_BASE_URL}/update`, profileData);
      return response.data;
    } catch (error) {
      console.error('Error updating profile:', error);
      throw error;
    }
  },

  // Đổi mật khẩu
  resetPassword: async (oldPassword, newPassword) => {
    try {
      const response = await axios.post(`${API_BASE_URL}/reset-password`, {
        oldPassword,
        newPassword
      });
      return response.data;
    } catch (error) {
      console.error('Error resetting password:', error.response?.data || error.message);
      throw error;
    }
  },

  // Tìm user theo email
  getUserByEmail: async (email) => {
    try {
      const response = await axios.get(`${API_BASE_URL}/email`, {
        params: { email }
      });
      return response.data;
    } catch (error) {
      console.error('User not found by email:', error);
      throw error;
    }
  },

  // Tìm user theo số điện thoại
  getUserByPhone: async (phone) => {
    try {
      const response = await axios.get(`${API_BASE_URL}/phone`, {
        params: { phone }
      });
      return response.data;
    } catch (error) {
      console.error('User not found by phone:', error);
      throw error;
    }
  },

  // Tìm user theo ID
  getUserById: async (id) => {
    try {
      const response = await axios.get(`${API_BASE_URL}/${id}`);
      return response.data;
    } catch (error) {
      console.error('User not found by ID:', error);
      throw error;
    }
  }
};

export default userService;
