import axios from 'axios';
import React, { useState } from 'react';

const ProductForm = ({ onSubmit, editingProduct }) => {
  const [formData, setFormData] = useState(editingProduct || {
    Ten: '', Gia: '', Chiphi: '', Loinhuan: '', Loai: '', Mota: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/products', formData);
      onSubmit(response.data);
    } catch (error) {
      console.error('Failed to create product:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="Ten" value={formData.Ten} onChange={handleChange} placeholder="Tên" />
      <input name="Gia" value={formData.Gia} onChange={handleChange} placeholder="Giá" />
      <input name="Chiphi" value={formData.Chiphi} onChange={handleChange} placeholder="Chi phí" />
      <input name="Loinhuan" value={formData.Loinhuan} onChange={handleChange} placeholder="Lợi nhuận" />
      <input name="Loai" value={formData.Loai} onChange={handleChange} placeholder="Loại" />
      <textarea name="Mota" value={formData.Mota} onChange={handleChange} placeholder="Mô tả" />
      <button type="submit">Lưu sản phẩm</button>
    </form>
  );
};

export default ProductForm;
