import React, { useState, useEffect } from "react";
import ProductForm from "../ProductForm/ProductForm";

const ProductFormModal = ({ show, onClose, onSubmit, editingProduct, categories }) => {
  const [product, setProduct] = useState({
    name: "",
    description: "",
    price: "",
    stock: "",
    category_id: "",
    category_id_combo: "",
    category_id_uu_dai: "",
    url_img: "",
  });

  useEffect(() => {
    if (editingProduct) {
      setProduct(editingProduct);
    } else {
      setProduct({
        name: "",
        description: "",
        price: "",
        stock: "",
        category_id: "",
        category_id_combo: "",
        category_id_uu_dai: "",
        url_img: "",
      });
    }
  }, [editingProduct]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProduct((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(product);
  };

  if (!show) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-box">
        <h2>{editingProduct ? "Chỉnh sửa sản phẩm" : "Thêm sản phẩm"}</h2>
        <form onSubmit={handleSubmit} className="product_form">
          <ProductForm
            product={product}
            onChange={handleChange}
            categories={categories}
          />
          <div className="product_actions">
            <button type="submit" className="btn btn-green">Lưu</button>
            <button type="button" onClick={onClose} className="btn btn-blue">Hủy</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProductFormModal;
