import React, { useEffect, useState } from 'react';
import {
  LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer
} from 'recharts';
import axios from 'axios';

function UserChart() {
  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchUserStats = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/analytics/customers-per-month');
        // Chuyển đổi dữ liệu nếu cần (ví dụ: {month: '2024-05', users: 30} => {month: 'T5', users: 30})
        const formatted = res.data.map(item => ({
          month: 'T' + new Date(item.month).getMonth() + 1,
          users: item.users
        }));
        setData(formatted);
      } catch (error) {
        console.error('❌ Lỗi khi lấy dữ liệu lượt khách theo tháng:', error);
      }
    };

    fetchUserStats();
  }, []);

  return (
    <div className="chart-container">
      <h3>Lượt khách theo tháng</h3>
      <ResponsiveContainer width="100%" height={300}>
        <LineChart data={data}>
          <CartesianGrid stroke="#ccc" />
          <XAxis dataKey="month" />
          <YAxis />
          <Tooltip />
          <Line type="monotone" dataKey="users" stroke="#8884d8" strokeWidth={2} />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
}

export default UserChart;
