import React, { useState, useEffect } from "react";
import ProductForm from "../ProductForm/ProductForm";

const ProductFormModal = ({ show, onClose, editingProduct, categories, onSubmit }) => {
  const [product, setProduct] = useState({
    name: "",
    description: "",
    price: 0,
    stock: 0,
    category_id: 5,
    category_id_combo: 3,
    category_id_uu_dai: "",
    url_img: ""
  });

  useEffect(() => {
    if (editingProduct) {
      setProduct(editingProduct);
    } else {
      setProduct({
        name: "",
        description: "",
        price: 0,
        stock: 0,
        category_id: 5,
        category_id_combo: 3,
        category_id_uu_dai: "",
        url_img: ""
      });
    }
  }, [editingProduct]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProduct((prev) => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(product);
  };

  if (!show) return null;

return (
  <div className={`modal ${show ? "active" : ""}`}>
    <div className="modal_overlay" onClick={onClose}></div>
    <div className="modal_content">
      <h2>{editingProduct ? "Chỉnh sửa sản phẩm" : "Thêm sản phẩm"}</h2>
      <form onSubmit={handleSubmit} className="product_form">
        <ProductForm
          product={product}
          onChange={handleChange}
          categories={categories}
        />
        <div className="product_actions">
          <button type="submit" className="update">Lưu</button>
          {/* <button type="button" onClick={onClose}>Hủy</button> */}
        </div>
      </form>
    </div>
  </div>
)};

export default ProductFormModal;
