import React, { useState, useEffect } from 'react';
import '../ProductForm/ProductForm';

const ProductFormModal = ({ show, onClose, editingProduct, categories, onSubmit }) => {
  const [product, setProduct] = useState({
    name: '',
    category_id_uu_dai: 0,
    price: '',
    category_id: '',
    category_id_combo: '',
    stock: '',
  });

  useEffect(() => {
    if (editingProduct) {
      setProduct(editingProduct);
    } else {
      setProduct({
        name: '',
        category_id_uu_dai: 0,
        price: '',
        category_id: '',
        category_id_combo: '',
        stock: '',
      });
    }
  }, [editingProduct]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProduct((prev) => ({
      ...prev,
      [name]: name === 'category_id_uu_dai' ? parseInt(value) : value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(product);
  };

  if (!show) return null;

  return (
    <div className="modal-overlay">
      <div className="modal">
        <h2>{editingProduct ? 'Sửa sản phẩm' : 'Thêm sản phẩm'}</h2>
        <form onSubmit={handleSubmit}>
          <label>
            Tên sản phẩm:
            <input
              type="text"
              name="name"
              value={product.name}
              onChange={handleChange}
              required
            />
          </label>

          <label>
            Ưu đãi:
            <select
              name="category_id_uu_dai"
              value={product.category_id_uu_dai}
              onChange={handleChange}
            >
              <option value={0}>Không</option>
              <option value={1}>✔️ Có</option>
            </select>
          </label>

          <label>
            Giá:
            <input
              type="number"
              name="price"
              value={product.price}
              onChange={handleChange}
              required
            />
          </label>

          <label>
            Danh mục:
            <select
              name="category_id"
              value={product.category_id}
              onChange={handleChange}
              required
            >
              <option value="">-- Chọn danh mục --</option>
              {categories.map((c) => (
                <option key={c.category_id} value={c.category_id}>
                  {c.name}
                </option>
              ))}
            </select>
          </label>

          <label>
            Loại combo:
            <select
              name="category_id_combo"
              value={product.category_id_combo}
              onChange={handleChange}
              required
            >
              <option value="">-- Chọn loại combo --</option>
              <option value={3}>Combo 1 người</option>
              <option value={4}>Combo nhóm</option>
            </select>
          </label>

          <label>
            Kho:
            <input
              type="number"
              name="stock"
              value={product.stock}
              onChange={handleChange}
              required
            />
          </label>

          <div className="modal-actions">
            <button type="submit">Lưu</button>
            <button type="button" onClick={onClose}>Hủy</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProductFormModal;
