import React from "react";
// import "./ProductTableNew.css";

const ProductTableNew = ({ title, products, onEdit, onDelete }) => {
  return (
    <div className="product-table-container">
      <h3>{title}</h3>
      <table className="product-table">
        <thead>
          <tr>
            <th>Tên sản phẩm</th>
            <th>Ưu đãi</th>
            <th>Giá</th>
            <th>Danh mục</th>
            <th>Kho</th>
            <th>Hành động</th>
          </tr>
        </thead>
        <tbody>
          {products?.map((product) => (
            <tr key={product.product_id}>
              <td>{product.name}</td>
              <td>{product.category_id_uu_dai === 1 ? "✔️" : ""}</td>
              <td>{product.price.toLocaleString()}₫</td>
              <td>{product.cate_name}</td>
              <td>{product.stock}</td>
              <td>
                <button onClick={() => onEdit(product)}>Sửa</button>
                <button onClick={() => onDelete(product.product_id)}>Xóa</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ProductTableNew;
