// services/orderService.js
import axios from "axios";

const API_URL = "http://localhost:8080/api/orders"; // backend Spring Boot

// Lấy danh sách đơn hàng
export const getOrders = async () => {
  const response = await axios.get(API_URL);
  return response.data;
};

// Tạo đơn hàng mới (bao gồm order + orderDetails)
export const createOrder = async (orderData) => {
  const response = await axios.post(API_URL, orderData);
  return response.data;
};

// Cập nhật trạng thái đơn hàng (nếu có API hỗ trợ)
export const updateOrderStatus = async (orderId, status) => {
  const response = await axios.patch(`${API_URL}/${orderId}/status`, { status });
  return response.data;
};
