import React, { useState } from "react";
import ProductModal from "../ProductModal/ProductModal"; // Import modal
import './ProductTable.css';
const products = [
  { name: "Cơm gà", price: "45.000đ", type: "Món gà rán", time: "Đăng 6 ngày trước", even: false },
  { name: "Cơm gà rán", price: "45.000đ", type: "Món gà rán", time: "Đăng 6 ngày trước", even: true },
  { name: "Cơm phi lê gà", price: "45.000đ", type: "Món gà rán", time: "Đăng 6 ngày trước", even: false },
  { name: "Gà viên", price: "30.000đ", type: "Món gà rán", time: "Đăng 6 ngày trước", even: true },
];

export default function ProductTable() {
  const [showModal, setShowModal] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);

  const handleViewMore = (product) => {
    setSelectedProduct(product);
    setShowModal(true);
  };

  return (
    <div className="jobs">
      <h2>
        Sản Phẩm{" "}
        <small
          onClick={() => handleViewMore(products[0])} // Ví dụ: lấy sp đầu tiên
          style={{ cursor: "pointer", color: "#3b49df" }}
        >
          Xem tất cả sản phẩm <span className="las la-arrow-right"></span>
        </small>
      </h2>
      <div className="table-responsive">
        <table width="100%">
          <tbody>
            {products.map((product, idx) => (
              <tr key={idx}>
                <td>
                  <span className={`indicator ${product.even ? "even" : ""}`}></span>
                </td>
                <td><div>{product.name}</div></td>
                <td><div>{product.price}</div></td>
                <td><div>{product.type}</div></td>
                <td><div>{product.time}</div></td>
                <td>
                  <button onClick={() => handleViewMore(product)}>Xem thêm</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Modal */}
      {showModal && (
        <ProductModal
          product={selectedProduct}
          onClose={() => setShowModal(false)}
        />
      )}
    </div>
  );
}
