import React, { useState, useEffect } from 'react';
import './ProductFormModal.css';
import ProductForm from './ProductForm'; // ✅ đảm bảo bạn đã import đúng

const ProductFormModal = ({ show, onClose, editingProduct, categories, onSubmit }) => {
  const [product, setProduct] = useState({
    name: '',
    description: '',
    price: '',
    stock: '',
    category_id: '',
    category_id_combo: '',
    category_id_uu_dai: '',
    url_img: '',
  });

  useEffect(() => {
    if (editingProduct) {
      setProduct(editingProduct);
    } else {
      setProduct({
        name: '',
        description: '',
        price: '',
        stock: '',
        category_id: '',
        category_id_combo: '',
        category_id_uu_dai: '',
        url_img: '',
      });
    }
  }, [editingProduct]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProduct((prev) => ({
      ...prev,
      [name]: name.includes('id') || name === 'stock' || name === 'price' ? parseInt(value) || '' : value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(product);
  };

  if (!show) return null; // ⛔ Ngăn render khi show=false

  return (
    <div className="modal-overlay">
      <div className="modal">
        <h2>{editingProduct ? 'Sửa sản phẩm' : 'Thêm sản phẩm'}</h2>
        <form onSubmit={handleSubmit}>
          <ProductForm product={product} onChange={handleChange} categories={categories} />
          <div className="modal-actions">
            <button type="submit">Lưu</button>
            <button type="button" onClick={onClose}>Hủy</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProductFormModal;
