import React, { useState } from 'react';
import './ProductFormModel.css';
import axios from 'axios';

const ProductFormModal = ({ onClose, onSubmit, initialData = {} }) => {
  const [formData, setFormData] = useState({
    name: initialData.name || '',
    price: initialData.price || '',
    otherCosts: initialData.otherCosts || '',
    profit: initialData.profit || '',
    category: initialData.category || '',
    description: initialData.description || '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/api/products', {
        Ten: formData.name,
        Gia: formData.price,
        Chiphi: formData.otherCosts,
        Loinhuan: formData.profit,
        Loai: formData.category,
        Mota: formData.description,
      });

      onSubmit(response.data); // Cập nhật danh sách ở cha
      onClose(); // Đóng modal
    } catch (error) {
      console.error('Lỗi khi gửi dữ liệu sản phẩm:', error);
      alert('Đã xảy ra lỗi khi gửi dữ liệu. Vui lòng thử lại!');
    }
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <h2>Thêm sản phẩm mới</h2>
        <form onSubmit={handleSubmit}>
          <label>Tên sản phẩm:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
          <label>Giá:</label>
          <input
            type="number"
            name="price"
            value={formData.price}
            onChange={handleChange}
            required
          />
          <label>Chi phí khác:</label>
          <input
            type="number"
            name="otherCosts"
            value={formData.otherCosts}
            onChange={handleChange}
          />
          <label>Lợi nhuận:</label>
          <input
            type="number"
            name="profit"
            value={formData.profit}
            onChange={handleChange}
          />
          <label>Loại:</label>
          <input
            type="text"
            name="category"
            value={formData.category}
            onChange={handleChange}
            required
          />
          <label>Mô tả:</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
          ></textarea>
          <div className="modal-buttons">
            <button type="submit">Thêm</button>
            <button type="button" onClick={onClose}>Hủy</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProductFormModal;
