// components/ProductTableNew/ProductTableNew.js
import React, { useEffect, useState } from "react";
import {
  getAllProducts,
  createProduct,
  updateProduct,
  deleteProductById,
} from "../../services/prodmanaService";

const ProductTableNew = () => {
  const [products, setProducts] = useState([]);
  const [editingProduct, setEditingProduct] = useState(null);
  const [newProduct, setNewProduct] = useState({
    name: "",
    description: "",
    price: 0,
    category_id: 5,
    category_id_combo: 3,
    category_id_uu_dai: 1,
    stock: 0,
    url_img: "",
  });

  useEffect(() => {
    getAllProducts()
      .then((data) => setProducts(data))
      .catch((err) => console.error(err));
  }, []);

  const handleAdd = () => {
    createProduct(newProduct)
      .then((res) => {
        setProducts([res, ...products]);
        setNewProduct({
          name: "",
          description: "",
          price: 0,
          category_id: 5,
          category_id_combo: 3,
          category_id_uu_dai: 1,
          stock: 0,
          url_img: "",
        });
      })
      .catch((err) => console.error(err));
  };

  const handleUpdate = () => {
    updateProduct(editingProduct.product_id, editingProduct)
      .then((res) => {
        setProducts((prev) =>
          prev.map((p) =>
            p.product_id === res.product_id ? res : p
          )
        );
        setEditingProduct(null);
      })
      .catch((err) => console.error(err));
  };

  const handleDelete = (id) => {
    deleteProductById(id)
      .then(() => {
        setProducts(products.filter((p) => p.product_id !== id));
      })
      .catch((err) => console.error(err));
  };

  return (
    <div>
      <h2 className="text-xl font-semibold mb-2">Thêm sản phẩm mới</h2>
      <input
        placeholder="Tên sản phẩm"
        value={newProduct.name}
        onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
      />
      {/* Thêm input cho các trường khác tương tự nếu cần */}
      <button onClick={handleAdd}>Thêm</button>

      <h2 className="text-xl font-semibold mt-6">Danh sách sản phẩm</h2>
      <table>
        <thead>
          <tr>
            <th>Tên</th>
            <th>Giá</th>
            <th>Kho</th>
            <th>Hành động</th>
          </tr>
        </thead>
        <tbody>
          {products.map((p) => (
            <tr key={p.product_id}>
              <td>
                {editingProduct?.product_id === p.product_id ? (
                  <input
                    value={editingProduct.name}
                    onChange={(e) =>
                      setEditingProduct({ ...editingProduct, name: e.target.value })
                    }
                  />
                ) : (
                  p.name
                )}
              </td>
              <td>{p.price}</td>
              <td>{p.stock}</td>
              <td>
                {editingProduct?.product_id === p.product_id ? (
                  <button onClick={handleUpdate}>Lưu</button>
                ) : (
                  <button onClick={() => setEditingProduct(p)}>Sửa</button>
                )}
                <button onClick={() => handleDelete(p.product_id)}>Xoá</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ProductTableNew;
