import { useEffect, useState } from 'react';
import axios from 'axios';

export default function Cards() {
  const [data, setData] = useState({
    revenue: 0,
    cancelled: 0,
    combo: 0,
  });

  useEffect(() => {
    async function fetchData() {
      try {
        const [revenueRes, cancelledRes, comboRes] = await Promise.all([
          axios.get('/api/dashboard/revenue'),
          axios.get('/api/dashboard/cancelled_orders'),
          axios.get('/api/dashboard/combo_orders')
        ]);

        setData({
          revenue: revenueRes.data.revenue || 0,
          cancelled: cancelledRes.data.cancelledRevenue || 0,
          combo: comboRes.data.comboRevenue || 0
        });
      } catch (err) {
        console.error('Error fetching dashboard data:', err);
      }
    }
    fetchData();
  }, []);

  const cards = [
    {
      title: "Doanh thu",
      subtitle: "Doanh thu bán hàng",
      value: `${data.revenue.toLocaleString()} đ`,
      trend: "Tăng 5%", // Placeholder
      className: "danger",
    },
    {
      title: "Hủy đơn",
      subtitle: "Giá trị của đơn hàng bị hủy",
      value: `${data.cancelled.toLocaleString()} đ`,
      trend: "Giảm 2%", // Placeholder
      className: "success",
    },
    {
      title: "Combo",
      subtitle: "Doanh thu các combo",
      value: `${data.combo.toLocaleString()} đ`,
      trend: "Ổn định", // Placeholder
      className: "yellow",
    },
  ];

  return (
    <div className="cards">
      {cards.map((card, idx) => (
        <div key={idx} className="card-single">
          <div className="card-flex">
            <div className="card-info">
              <div className="card-head">
                <span>{card.title}</span>
                <small>{card.subtitle}</small>
              </div>
              <h2>{card.value}</h2>
              <small>{card.trend}</small>
            </div>
            <div className={`card-chart ${card.className}`}>
              <span className="las la-chart-line"></span>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}
