import React, { useState, useEffect } from 'react';
import './ProductFormModal.css';

const ProductFormModal = ({ show, onClose, editingProduct, categories, onSubmit }) => {
  const [product, setProduct] = useState({
    name: '',
    promotion: '',
    price: '',
    category_id: '',
    category_id_combo: '',
    stock: '',
  });

  useEffect(() => {
    if (editingProduct) {
      setProduct(editingProduct);
    } else {
      setProduct({
        name: '',
        promotion: '',
        price: '',
        category_id: '',
        category_id_combo: '',
        stock: '',
      });
    }
  }, [editingProduct]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProduct((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(product);
  };

  if (!show) return null;

  return (
    <div className="modal-overlay">
      <div className="modal">
        <h2>{editingProduct ? 'Sửa sản phẩm' : 'Thêm sản phẩm'}</h2>
        <form onSubmit={handleSubmit}>
          <input type="text" name="name" value={product.name} onChange={handleChange} placeholder="Tên sản phẩm" required />
          <input type="text" name="promotion" value={product.promotion} onChange={handleChange} placeholder="Ưu đãi" />
          <input type="number" name="price" value={product.price} onChange={handleChange} placeholder="Giá" required />
          <select name="category_id" value={product.category_id} onChange={handleChange} required>
            <option value="">-- Danh mục --</option>
            {categories.map((c) => (
              <option key={c.category_id} value={c.category_id}>{c.name}</option>
            ))}
          </select>
          <select name="category_id_combo" value={product.category_id_combo} onChange={handleChange} required>
            <option value="">-- Loại combo --</option>
            <option value="3">Combo 1 người</option>
            <option value="4">Combo nhóm</option>
          </select>
          <input type="number" name="stock" value={product.stock} onChange={handleChange} placeholder="Kho" required />
          <div className="modal-actions">
            <button type="submit">Lưu</button>
            <button type="button" onClick={onClose}>Hủy</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProductFormModal;
