import React, { useState, useEffect } from "react";
import { getProducts } from "../../services/productService"; // c·∫ßn c√≥ h√†m n√†y

const IngredientsFormModal = ({ isOpen, onClose, onSubmit, ingredient, isEditMode }) => {
  const [formData, setFormData] = useState({
    productName: "",
    productId: null,
    name: "",
    quantity: "",
    unit: "",
    type: "",
    price: ""
  });

  const [products, setProducts] = useState([]);

  useEffect(() => {
  if (ingredient) {
    const matchedProduct = products.find(p => p.productId === ingredient.productId);
    setFormData({
      ...ingredient,
      productName: matchedProduct ? matchedProduct.name : ""
    });
  } else {
    setFormData({
      productName: "",
      productId: null,
      name: "",
      quantity: "",
      unit: "",
      type: "",
      price: ""
    });
  }
}, [ingredient, products]); // üëà th√™m c·∫£ `products` ·ªü dependency

  useEffect(() => {
    getProducts().then(setProducts);
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    let updated = { ...formData, [name]: value };

    if (name === "productName") {
      const found = products.find(p => p.name === value);
      updated.productId = found ? found.productId : null;
    }

    setFormData(updated);
  };

  const handleSubmit = (e) => {
  e.preventDefault();

  const payload = isEditMode
    ? {
        ...ingredient, // gi·ªØ to√†n b·ªô d·ªØ li·ªáu c≈©
        quantity: formData.quantity,
        price: formData.price
      }
    : formData;

  onSubmit(payload);
};

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal">
        <h2>{isEditMode ? "S·ª≠a nguy√™n li·ªáu" : "Th√™m nguy√™n li·ªáu"}</h2>
        <form onSubmit={handleSubmit}>
          {!isEditMode && (
            <>
              <label>M√≥n ƒÉn (productName)</label>
              <input type="text" name="productName" value={formData.productName} onChange={handleChange} required />

              <label>T√™n nguy√™n li·ªáu</label>
              <input type="text" name="name" value={formData.name} onChange={handleChange} required />

              <label>Lo·∫°i</label>
              <input type="text" name="type" value={formData.type} onChange={handleChange} required />

              <label>ƒê∆°n v·ªã</label>
              <input type="text" name="unit" value={formData.unit} onChange={handleChange} required />
            </>
          )}

          <label>SL/KL</label>
          <input type="number" name="quantity" value={formData.quantity} onChange={handleChange} required />

          <label>ƒê∆°n gi√°</label>
          <input type="number" name="price" value={formData.price} onChange={handleChange} required />

          <button type="submit">L∆∞u</button>
          <button type="button" onClick={onClose}>H·ªßy</button>
        </form>
      </div>
    </div>
  );
};

export default IngredientsFormModal;
