import axios from 'axios';

const API_BASE_URL = 'http://localhost:8080/api/orders';

const orderService = {
  // Lấy danh sách đơn hàng của người dùng hiện tại (qua JWT)
  getOrders: async () => {
    try {
      const response = await axios.get(API_BASE_URL);
      return response.data;
    } catch (error) {
      console.error('Error fetching orders:', error);
      throw error;
    }
  },

  // Gửi yêu cầu tạo đơn hàng mới
  placeOrder: async (orderRequest) => {
    try {
      const response = await axios.post(API_BASE_URL, orderRequest);
      return response.data;
    } catch (error) {
      console.error('Error placing order:', error.response?.data || error.message);
      throw error;
    }
  },

  // Cập nhật trạng thái đơn hàng
  updateOrderStatus: async (orderId, status) => {
    try {
      const response = await axios.patch(`${API_BASE_URL}/${orderId}/status`, { status });
      return response.data;
    } catch (error) {
      console.error('Error updating order status:', error.response?.data || error.message);
      throw error;
    }
  }
};

export default orderService;
