// src/components/Charts/UserChart.js
import { useEffect } from 'react';
import 'morris.js/morris.css';
import Morris from 'morris.js/morris';
import Raphael from 'raphael';

window.Raphael = Raphael; // Morris.js yêu cầu Raphael ở global scope

function UserChart() {
  useEffect(() => {
    // Xóa biểu đồ cũ nếu có
    document.getElementById('myfirstchart').innerHTML = '';

    // Tạo biểu đồ mới
    new Morris.Line({
      element: 'myfirstchart',
      data: [
        { year: '2020', value: 50 },
        { year: '2021', value: 75 },
        { year: '2022', value: 90 },
        { year: '2023', value: 120 },
        { year: '2024', value: 140 },
      ],
      xkey: 'year',
      ykeys: ['value'],
      labels: ['Số người dùng'],
      lineColors: ['#1e88e5'],
      resize: true,
    });
  }, []);

  return (
    <div className="chart-users">
      <div className="chart-head">
        <h3>Số khách hàng</h3>
        <span className="las la-cog"></span>
      </div>
      <div id="myfirstchart" style={{ height: '350px', width: '100%' }}></div>
    </div>
  );
}

export default UserChart;
