import React, { useEffect, useState } from 'react';
import { getAllProducts, createProduct, updateProduct, deleteProductById } from '../../services/prodmanaService';
import ProductTableNew from '../../components/ProductTableNew/ProductTableNew';
import ProductFormModal from '../../components/ProductFormModel/ProductFormModel';
import '../../assets/prodmana.css';

const Prodmana = () => {
  const [products, setProducts] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingProduct, setEditingProduct] = useState(null);

  const fetchProducts = async () => {
    try {
      const data = await getAllProducts();
      setProducts(data);
    } catch (error) {
      alert('Lỗi khi tải sản phẩm!');
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const handleAddOrUpdate = async (productData) => {
    try {
      if (editingProduct) {
        await updateProduct(editingProduct.product_id, productData);
        alert('Cập nhật thành công!');
      } else {
        await createProduct(productData);
        alert('Thêm sản phẩm thành công!');
      }
      setShowForm(false);
      setEditingProduct(null);
      fetchProducts(); // làm mới danh sách
    } catch (err) {
      alert('Lỗi khi thêm/cập nhật sản phẩm!');
    }
  };

  const handleEdit = (product) => {
    setEditingProduct(product);
    setShowForm(true);
  };

  const handleDelete = async (productId) => {
    if (window.confirm('Bạn chắc chắn muốn xoá sản phẩm này?')) {
      try {
        await deleteProductById(productId);
        fetchProducts();
        alert('Xoá thành công!');
      } catch {
        alert('Lỗi khi xoá!');
      }
    }
  };

  return (
    <div className="prodmana-container">
      <h1>Quản lý sản phẩm</h1>
      <button onClick={() => { setEditingProduct(null); setShowForm(true); }}>
        + Thêm sản phẩm
      </button>

      <ProductTableNew products={products} onEdit={handleEdit} onDelete={handleDelete} />

      <ProductFormModal
        show={showForm}
        onClose={() => setShowForm(false)}
        editingProduct={editingProduct}
        onSubmit={handleAddOrUpdate}
        onDeleted={fetchProducts}
      />
    </div>
  );
};

export default Prodmana;
