import React, { useState } from 'react';
//import Sidebar from '../../components/Sidebar/Sidebar';
import Navbar from '../../components/Navbar/Navbar';
import Header from '../../components/Header/Header';
import ProdProductTable from '../../components/ProdProductTable/ProdProductTable'; 
import ProductForm from '../../components/ProductForm/ProductForm';
// import Modal from '../../components/Modal/Modal';
import ProductFormModel from '../../components/ProductFormModel/ProductFormModel';
import '../../assets/prodmana.css';

const ProductManagement = () => {
  const [products, setProducts] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editingProduct, setEditingProduct] = useState(null);

  const handleAddProduct = () => {
    setEditingProduct(null);
    setShowModal(true);
  };

  const handleEditProduct = (product) => {
    setEditingProduct(product);
    setShowModal(true);
  };

  const handleFormSubmit = (product) => {
    if (editingProduct) {
      // Cập nhật sản phẩm
      setProducts(products.map((p) => (p.id === product.id ? product : p)));
    } else {
      // Thêm sản phẩm mới
      const newProduct = { ...product, id: Date.now() }; // giả lập id
      setProducts([...products, newProduct]);
    }
  };

  const handleDeleteProduct = (id) => {
    setProducts(products.filter((p) => p.id !== id));
  };

  return (
    <div className="product-management-container">
      <h1>Quản lý sản phẩm</h1>
      <button className="add-button" onClick={handleAddProduct}>+ Thêm sản phẩm</button>
      <ProductTable
        products={products}
        onEdit={handleEditProduct}
        onDelete={handleDeleteProduct}
      />
      <ProductFormModal
        show={showModal}
        onClose={() => setShowModal(false)}
        onSubmit={handleFormSubmit}
        editingProduct={editingProduct}
      />
    </div>
  );
};

export default ProductManagement;
