// OrderManagement.js
import React, { useState, useEffect } from 'react';
import { getOrders } from '../../services/orderService';
//import Sidebar from '../../components/Sidebar/Sidebar';
import Navbar from '../../components/Navbar/Navbar';
import Header from '../../components/Header/Header';
import OrderTable from '../../components/OrderTable/OrderTable';
import OrderFormModal from '../../components/OrderFormModal/OrderFormModel'; 
import OrderDetailModal from '../../components/OrderDetailModel/OrderDetailModel'; 
import '../../assets/ordersmana.css';

const OrderManagement = () => {
  const [orders, setOrders] = useState([]);
  const [showFormModal, setShowFormModal] = useState(false);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [orderDetails, setOrderDetails] = useState([]);
  const handleAddOrder = () => {
    setSelectedOrder(null);
    setShowFormModal(true);
  };

// const handleViewDetails = async (order) => {
//   try {
//     const res = await axios.get(`http://localhost:5000/api/orders/details/${order.order_id}`);
//     setOrderDetails(res.data);
//     setSelectedOrder(order);
//     setShowDetailModal(true);
//   } catch (error) {
//     console.error("Lỗi khi lấy chi tiết đơn hàng:", error);
//   }
// };

  const handleViewDetail = (order) => {
    setSelectedOrder(order);
    setShowDetailModal(true);
  };
    const fetchOrders = async () => {
    const data = await getOrders();
    setOrders(data);
  };

  useEffect(() => {
    fetchOrders();
  }, []);
  return (
    <div className="admin-page">
      <Navbar />
      <div className="main-content">
        <Header />
        {/* <main> */}
          <section className="orders">
            <h2>Danh sách đơn hàng</h2>
            <button id="addOrderBtn" onClick={handleAddOrder}>Thêm đơn hàng</button>
          </section>
        {/* </main> */}
      </div>
      <OrderTable 
        orders={orders} 
        onEdit={(order) => {
          setSelectedOrder(order);
          setShowFormModal(true);
        }}
        onView={(order) => {
          setSelectedOrder(order);
          setShowDetailModal(true);
        }}
      />
      <OrderFormModal
        show={showFormModal}
        onClose={() => setShowFormModal(false)}
        order={selectedOrder}
      />
      {/* <OrderDetailModal
        show={showDetailModal}
        onClose={() => setShowDetailModal(false)}
        order={selectedOrder}
      /> */}
    {/* <OrderDetailModal
      isOpen={showDetailModal}
      onClose={() => setShowDetailModal(false)}
      order={selectedOrder}
      details={selectedOrder}
    /> */}
    {/* <OrderDetailModal
      isOpen={showDetailModal}
      onClose={() => setShowDetailModal(false)}
      order={selectedOrder}
      details={selectedOrder?.orderDetails || []}
    /> */}
    <OrderDetailModal
      order={selectedOrder}
      details={orderDetails}
      isOpen={showDetailModal}
      onClose={() => setShowDetailModal(false)}
    />

    </div>
  );
};

export default OrderManagement;
