import React from "react";

const ProductTableNew = ({ products, categories, onEdit, onDelete }) => {
  const renderTable = (filteredProducts, title) => (
    <div>
      <h2 className="text-xl font-semibold mt-6 mb-2">{title}</h2>
      <table className="product-table">
        <thead>
          <tr>
            <th>Tên sản phẩm</th>
            <th>Ưu đãi</th>
            <th>Giá</th>
            <th>Danh mục</th>
            <th>Kho</th>
            <th>Hành động</th>
          </tr>
        </thead>
        <tbody>
          {filteredProducts.map((product) => {
            const categoryName =
              categories.find((cat) => cat.category_id === product.category_id)?.category_name || "Không rõ";

            return (
              <tr key={product.product_id}>
                <td>{product.product_name}</td>
                <td>{product.category_id_uu_dai ? "Có" : "Không"}</td>
                <td>{product.price}₫</td>
                <td>{categoryName}</td>
                <td>{product.stock}</td>
                <td>
                  <button onClick={() => onEdit(product)}>Sửa</button>
                  <button onClick={() => onDelete(product.product_id)}>Xóa</button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );

  const combo1Nguoi = products.filter((p) => p.category_id_combo === 3);
  const comboNhom = products.filter((p) => p.category_id_combo === 4);

  return (
    <div className="product-tables">
      {renderTable(combo1Nguoi, "Combo 1 người")}
      {renderTable(comboNhom, "Combo nhóm")}
    </div>
  );
};
console.log("Products:", products);
console.log("Combo 1 người:", combo1Nguoi);
console.log("Combo nhóm:", comboNhom);
export default ProductTableNew;
