// components/ProductFormModal/ProductFormModal.js
import React, { useEffect, useState } from 'react';
import './ProductFormModal.css';
import ProductForm from '../ProductForm/ProductForm';
import axios from 'axios';

const ProductFormModal = ({ show, onClose, onSubmit, editingProduct, onDeleted }) => {
  const [categories, setCategories] = useState([]);

  // ✅ Gọi API để lấy danh mục từ bảng categories khi mở modal
  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/categories');
        setCategories(res.data);
      } catch (error) {
        console.error('Lỗi khi tải danh mục:', error);
      }
    };

    if (show) {
      fetchCategories();
    }
  }, [show]);

  if (!show) return null;

  const handleDelete = async () => {
    if (!editingProduct) return;

    const confirm = window.confirm(`Bạn có chắc muốn xóa sản phẩm "${editingProduct.name}" không?`);
    if (!confirm) return;

    try {
      await axios.delete(`http://localhost:5000/api/products/${editingProduct.product_id}`);
      alert('Đã xóa sản phẩm thành công!');
      if (onDeleted) onDeleted();
      onClose();
    } catch (error) {
      console.error('Lỗi xoá sản phẩm:', error);
      alert('Lỗi khi xoá sản phẩm!');
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>{editingProduct ? 'Cập nhật sản phẩm' : 'Thêm sản phẩm mới'}</h2>

        {/* ✅ Truyền categories xuống ProductForm */}
        <ProductForm
          onSubmit={onSubmit}
          editingProduct={editingProduct}
          onCancel={onClose}
          categories={categories}
        />

        <div className="form-buttons">
          {editingProduct && (
            <button type="button" className="delete-btn" onClick={handleDelete}>
              Xóa sản phẩm
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProductFormModal;
