import React, { useState, useEffect } from 'react';
//import Sidebar from '../../components/Sidebar/Sidebar';
import Navbar from '../../components/Navbar/Navbar';
import Header from '../../components/Header/Header';
import ProdProductTable from '../../components/ProdProductTable/ProdProductTable'; 
import {
  getAllProducts,
  createProduct,
  updateProduct,
} from '../services/prodmanaService';

// import Modal from '../../components/Modal/Modal';
import ProductFormModal from '../../components/ProductFormModel/ProductFormModel';
import '../../assets/prodmana.css';

const ProductManagement = () => {
  const [products, setProducts] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editingProduct, setEditingProduct] = useState(null);

  const fetchProducts = async () => {
    try {
      const data = await getAllProducts();
      setProducts(data);
    } catch (err) {
      console.error('Lỗi khi tải sản phẩm:', err);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const handleAddClick = () => {
    setEditingProduct(null); // reset nếu thêm mới
    setShowModal(true);
  };

  const handleSubmit = async (productData) => {
    try {
      if (editingProduct) {
        await updateProduct(editingProduct.product_id, productData);
      } else {
        await createProduct(productData);
      }
      setShowModal(false);
      fetchProducts(); // cập nhật lại danh sách
    } catch (err) {
      alert('Lỗi khi gửi dữ liệu');
    }
  };

  return (
    <div className="admin-page">
      <Navbar />
      <div className="main-content">
        <Header />
        <main>
           <h1>Quản lý sản phẩm</h1>
      <button onClick={handleAddClick}>Thêm sản phẩm</button>

      <table border="1" style={{ marginTop: '1rem', width: '100%' }}>
        <thead>
          <tr>
            <th>ID</th><th>Tên</th><th>Giá</th><th>Kho</th><th>Hành động</th>
          </tr>
        </thead>
        <tbody>
          {products.map((p) => (
            <tr key={p.product_id}>
              <td>{p.product_id}</td>
              <td>{p.name}</td>
              <td>{p.price}</td>
              <td>{p.stock}</td>
              <td>
                <button onClick={() => { setEditingProduct(p); setShowModal(true); }}>
                  Sửa / Xoá
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
        </main>
      </div>

      <ProductFormModal
        show={showModal}
        onClose={() => setShowModal(false)}
        onSubmit={handleSubmit}
        editingProduct={editingProduct}
        onDeleted={fetchProducts}
      />
    </div>
  );
};

export default ProductManagement;
