import React, { useState } from 'react';
import Navbar from '../../components/Navbar/Navbar';
import Header from '../../components/Header/Header';
import Sidebar from '../../components/Sidebar/Sidebar';
import ProductTable from '../../components/ProductTable/ProductTable';
import ProductForm from '../../components/ProductForm/ProductForm';
import Modal from '../../components/Modal/Modal';

import '../../assets/prodmana.css'; // Nếu bạn có CSS riêng cho trang này

const ProductManagementPage = () => {
  const [products, setProducts] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editingProduct, setEditingProduct] = useState(null);

  const handleAddOrUpdate = (product) => {
    if (editingProduct) {
      setProducts(products.map(p => (p === editingProduct ? product : p)));
      setEditingProduct(null);
    } else {
      setProducts([...products, product]);
    }
    setShowModal(false);
  };

  const handleEdit = (product) => {
    setEditingProduct(product);
    setShowModal(true);
  };

  const handleDelete = (product) => {
    setProducts(products.filter(p => p !== product));
  };

  return (
    <div className="dashboard">
      <Navbar />
      <div className="dashboard_container">
        <Sidebar />
        <div className="dashboard_content">
          <Header />
          <div className="products">
            <div className="products_header">
              <h2>Danh sách sản phẩm</h2>
              <button onClick={() => { setEditingProduct(null); setShowModal(true); }}>
                Thêm sản phẩm
              </button>
            </div>

            <ProductTable
              products={products}
              onEdit={handleEdit}
              onDelete={handleDelete}
            />

            <Modal show={showModal} onClose={() => setShowModal(false)}>
              <h2>{editingProduct ? 'Cập nhật sản phẩm' : 'Thêm sản phẩm mới'}</h2>
              <ProductForm
                onSubmit={handleAddOrUpdate}
                editingProduct={editingProduct}
                onCancel={() => { setShowModal(false); setEditingProduct(null); }}
              />
            </Modal>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductManagement;
