import { useState, useEffect } from 'react';
import './OrderFormModel.css';
import { getAllUsers, getUserById } from '../../services/userService';

const OrderFormModal = ({ show, onClose, order, onSave }) => {
  const [users, setUsers] = useState([]);
  const [formData, setFormData] = useState({
    user_id: order?.user_id || '',
    status: order?.status || 'PENDING',
    total_price: order?.total_price || '',
  });
  const [isNewUser, setIsNewUser] = useState(false);
  const [newUserName, setNewUserName] = useState('');

  useEffect(() => {
    if (show) {
      fetchUsers();
    }
  }, [show]);

  useEffect(() => {
    if (order) {
      setFormData({
        user_id: order.user_id || '',
        status: order.status || 'PENDING',
        total_price: order.total_price || '',
      });
    }
  }, [order]);

  const fetchUsers = async () => {
    try {
      const res = await getAllUsers();
      setUsers(res);
    } catch (err) {
      console.error('Lỗi khi tải danh sách người dùng:', err);
    }
  };

  const handleUserChange = (e) => {
    const value = e.target.value;
    if (value === 'new') {
      setIsNewUser(true);
      setFormData((prev) => ({ ...prev, user_id: '' }));
    } else {
      setIsNewUser(false);
      setFormData((prev) => ({ ...prev, user_id: value }));
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (isNewUser && newUserName.trim()) {
      try {
        const response = await fetch('/api/users', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ full_name: newUserName }),
        });
        const newUser = await response.json();
        formData.user_id = newUser.user_id;
      } catch (err) {
        alert('❌ Lỗi khi thêm khách hàng mới');
        return;
      }
    }

    onSave(formData);
  };

  if (!show) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>{order ? 'Chỉnh sửa đơn hàng' : 'Thêm đơn hàng mới'}</h2>
        <form className="order-form" onSubmit={handleSubmit}>
          <label>Khách hàng:</label>
          <select name="user_id" value={formData.user_id} onChange={handleUserChange} required>
            <option value="">-- Chọn khách hàng --</option>
            {users.map((u) => (
              <option key={u.user_id} value={u.user_id}>
                {u.full_name}
              </option>
            ))}
            <option value="new">* Thêm khách hàng mới *</option>
          </select>

          {isNewUser && (
            <>
              <label>Tên khách hàng mới:</label>
              <input
                type="text"
                value={newUserName}
                onChange={(e) => setNewUserName(e.target.value)}
                required
              />
            </>
          )}

          <label>Trạng thái:</label>
          <select name="status" value={formData.status} onChange={handleChange}>
            <option value="PENDING">PENDING</option>
            <option value="CONFIRMED">CONFIRMED</option>
            <option value="SHIPPED">SHIPPED</option>
            <option value="CANCELLED">CANCELLED</option>
          </select>

          <label>Giá đơn hàng:</label>
          <input
            type="number"
            name="total_price"
            value={formData.total_price}
            onChange={handleChange}
            required
          />

          <div className="modal-actions">
            <button type="submit" className="btn-save">Lưu</button>
            <button type="button" className="btn-cancel" onClick={onClose}>Hủy</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default OrderFormModal;
