import React, { useState, useEffect } from 'react';
import axios from 'axios';

import Navbar from '../../components/Navbar/Navbar';
import Header from '../../components/Header/Header';
import ProdProductTable from '../../components/ProdProductTable/ProdProductTable';
import ProductFormModel from '../../components/ProductFormModel/ProductFormModel';
import '../../assets/prodmana.css';

const ProductManagement = () => {
  const [products, setProducts] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editingProduct, setEditingProduct] = useState(null);

  const API_URL = 'http://localhost:5000/api/products';

  // ðŸ“¥ Láº¥y danh sÃ¡ch sáº£n pháº©m tá»« server
  useEffect(() => {
    axios.get(API_URL)
      .then(res => setProducts(res.data))
      .catch(err => console.error('Lá»—i táº£i sáº£n pháº©m:', err));
  }, []);

  // âœ… ThÃªm hoáº·c cáº­p nháº­t sáº£n pháº©m
  const handleAddOrUpdate = (product) => {
    if (editingProduct) {
      // Logic cáº­p nháº­t náº¿u cÃ³ (tÃ¹y backend há»— trá»£ PUT)
      console.log("TÃ­nh nÄƒng cáº­p nháº­t chÆ°a Ä‘Æ°á»£c triá»ƒn khai.");
      setEditingProduct(null);
    } else {
      axios.post(API_URL, product)
        .then(res => {
          setProducts([...products, res.data]);
        })
        .catch(err => console.error('Lá»—i thÃªm sáº£n pháº©m:', err));
    }

    setShowModal(false);
  };

  const handleEdit = (product) => {
    setEditingProduct(product);
    setShowModal(true);
  };

  const handleDelete = (product) => {
    // CÃ³ thá»ƒ tÃ­ch há»£p DELETE sau náº¿u backend há»— trá»£
    setProducts(products.filter(p => p.id !== product.id));
  };

  return (
    <div className="admin-page">
      <Navbar />
      <div className="main-content">
        <Header />
        <main>
          <section className="product-management">
            <div className="products_header">
              <h2>Danh sÃ¡ch sáº£n pháº©m</h2>
              <button
                className="add-button"
                onClick={() => {
                  setEditingProduct(null);
                  setShowModal(true);
                }}
              >
                ThÃªm sáº£n pháº©m
              </button>
            </div>
            <ProdProductTable products={products} onEdit={handleEdit} onDelete={handleDelete} />
          </section>
        </main>
      </div>

      <ProductFormModel
        show={showModal}
        onClose={() => {
          setShowModal(false);
          setEditingProduct(null);
        }}
        onSubmit={handleAddOrUpdate}
        editingProduct={editingProduct}
      />
    </div>
  );
};

export default ProductManagement;
