import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './ProductForm.css';

const ProductForm = ({ onProductSubmit, editingProduct, onCancelEdit }) => {
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    category_id: '',
    stock: '',
    sold_quantity: '',
    url_img: ''
  });

  const [categories, setCategories] = useState([]);

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const res = await axios.get('http://localhost:8080/api/categories');
        setCategories(res.data);
      } catch (error) {
        console.error('Lỗi khi lấy danh sách danh mục:', error);
      }
    };

    fetchCategories();
  }, []);

  useEffect(() => {
    if (editingProduct) {
      const {
        name = '',
        description = '',
        price = '',
        category_id = '',
        stock = '',
        sold_quantity = '',
        url_img = ''
      } = editingProduct;

      setFormData({
        name,
        description,
        price,
        category_id: String(category_id), // ÉP KIỂU về chuỗi
        stock,
        sold_quantity,
        url_img
      });
    }
  }, [editingProduct]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onProductSubmit(formData);
    setFormData({
      name: '',
      description: '',
      price: '',
      category_id: '',
      stock: '',
      sold_quantity: '',
      url_img: ''
    });
  };

  return (
    <form className="product-form" onSubmit={handleSubmit}>
      <h2>{editingProduct ? 'Cập nhật sản phẩm' : 'Thêm sản phẩm'}</h2>

      <label>Tên sản phẩm:</label>
      <input
        type="text"
        name="name"
        value={formData.name}
        onChange={handleChange}
        required
      />

      <label>Mô tả:</label>
      <textarea
        name="description"
        value={formData.description}
        onChange={handleChange}
        required
      />

      <label>Giá:</label>
      <input
        type="number"
        name="price"
        value={formData.price}
        onChange={handleChange}
        required
      />

      <label>Danh mục:</label>
      <select
        name="category_id"
        value={formData.category_id}
        onChange={handleChange}
        required
      >
        <option value="">-- Chọn danh mục --</option>
        {categories.map((cat) => (
          <option key={cat.category_id} value={String(cat.category_id)}>
            {cat.cate_name}
          </option>
        ))}
      </select>

      <label>Số lượng tồn kho:</label>
      <input
        type="number"
        name="stock"
        value={formData.stock}
        onChange={handleChange}
        required
      />

      <label>Số lượng đã bán:</label>
      <input
        type="number"
        name="sold_quantity"
        value={formData.sold_quantity}
        onChange={handleChange}
        required
      />

      <label>URL ảnh:</label>
      <input
        type="text"
        name="url_img"
        value={formData.url_img}
        onChange={handleChange}
        required
      />

      <div className="form-buttons">
        <button type="submit">{editingProduct ? 'Cập nhật' : 'Thêm mới'}</button>
        {editingProduct && <button type="button" onClick={onCancelEdit}>Hủy</button>}
      </div>
    </form>
  );
};

export default ProductForm;
