import React, { useEffect, useState } from "react";
import { getAllProducts } from "../../services/dashboardService";

export default function ProductTable() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    getAllProducts().then(data => {
      // Thêm trường `even` dựa trên index
      const processed = data.map((item, index) => ({
        ...item,
        even: index % 2 === 0,
        price: `${item.price}đ`, // nếu backend trả về số thì thêm đơn vị
        time: item.posted || "Đăng chưa rõ thời gian",
      }));
      setProducts(processed);
    });
  }, []);

  return (
    <div className="jobs">
      <h2>
        Sản Phẩm{" "}
        <small>
          Xem tất cả sản phẩm <span className="las la-arrow-right"></span>
        </small>
      </h2>
      <div className="table-responsive">
        <table width="100%">
          <tbody>
            {products.map((product, idx) => (
              <tr key={idx}>
                <td>
                  <div>
                    <span className={`indicator ${product.even ? "even" : ""}`}></span>
                  </div>
                </td>
                <td><div>{product.name}</div></td>
                <td><div>{product.price}</div></td>
                <td><div>{product.type}</div></td>
                <td><div>{product.time}</div></td>
                <td>
                  <div>
                    <button onClick={() => window.location.href = `/products/${product.id}`}>
                      Xem thêm
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
