// components/ProductFormModal/ProductFormModal.js
import React from 'react';
import './ProductFormModel.css';
import ProductForm from '../ProductForm/ProductForm';
import { deleteProductById } from '../../services/prodmanaService';

const ProductFormModal = ({ show, onClose, onSubmit, editingProduct, onDeleted }) => {
  if (!show) return null;

  const handleDelete = async () => {
    if (!editingProduct) return;

    const confirm = window.confirm(`Bạn có chắc muốn xóa sản phẩm "${editingProduct.name}" không?`);
    if (!confirm) return;

    try {
      await deleteProductById(editingProduct.product_id);
      alert('Đã xóa sản phẩm thành công!');
      if (onDeleted) onDeleted(); // reload danh sách bên ngoài
      onClose();
    } catch (error) {
      alert('Lỗi khi xoá sản phẩm!');
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>{editingProduct ? 'Cập nhật sản phẩm' : 'Thêm sản phẩm mới'}</h2>
        <ProductForm
          onSubmit={onSubmit}
          editingProduct={editingProduct}
          onCancel={onClose}
        />
        <div className="form-buttons">
          {/* <button type="button" className="cancel-btn" onClick={onClose}>
            Hủy
          </button> */}

          {/* Thêm nút xoá nếu đang sửa sản phẩm */}
          {editingProduct && (
            <button type="button" className="delete-btn" onClick={handleDelete}>
              Xóa sản phẩm
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProductFormModal;
