// components/ProductForm/ProductForm.js
import React, { useState, useEffect } from 'react';
import { getCategories } from '../../services/categoryService';

const ProductForm = ({ onSubmit, onCancel, editingProduct }) => {
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    category_id: '',
    stock: '',
    sold_quantity: '',
    url_img: ''
  });

  const [categories, setCategories] = useState([]);

  useEffect(() => {
    // Lấy danh sách categories từ API
    getCategories()
      .then((res) => setCategories(res))
      .catch((err) => console.error('Lỗi khi lấy danh mục:', err));
  }, []);

  useEffect(() => {
    if (editingProduct) {
      setFormData(editingProduct);
    }
  }, [editingProduct]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <form className="product-form" onSubmit={handleSubmit}>
      <label>Tên sản phẩm:</label>
      <input name="name" value={formData.name} onChange={handleChange} required />

      <label>Mô tả:</label>
      <input name="description" value={formData.description} onChange={handleChange} />

      <label>Giá:</label>
      <input type="number" name="price" value={formData.price} onChange={handleChange} required />

      <label>Danh mục:</label>
      <select name="category_id" value={formData.category_id} onChange={handleChange} required>
        <option value="">-- Chọn danh mục --</option>
        {categories.map((cat) => (
          <option key={cat.category_id} value={cat.category_id}>{cat.name}</option>
        ))}
      </select>

      <label>Số lượng trong kho:</label>
      <input type="number" name="stock" value={formData.stock} onChange={handleChange} />

      <label>Đã bán:</label>
      <input type="number" name="sold_quantity" value={formData.sold_quantity} onChange={handleChange} />

      <label>Link hình ảnh:</label>
      <input name="url_img" value={formData.url_img} onChange={handleChange} />

      <div className="form-buttons">
        <button type="submit" className="submit-btn">
          {editingProduct ? 'Cập nhật' : 'Thêm'}
        </button>
        <button type="button" className="cancel-btn" onClick={onCancel}>Hủy</button>
      </div>
    </form>
  );
};

export default ProductForm;
