import { updateProduct } from '../../api/productAPI';
import './ProductForm.css';
const ProductForm = ({ product, onChange, categories }) => {
  function onChange(e) {
    const { name, value } = e.target;
    const val = value === "" ? null : Number(value);
    updateProduct(prev => ({
      ...prev,
      [name]: val,
    }));
  }

  return (
    <div className="product-form">
      <div className="form-group">
        <label>Tên sản phẩm:</label>
        <input type="text" name="name" value={product.name} onChange={onChange} className="form-input" />
      </div>

      <div className="form-group">
        <label>Mô tả:</label>
        <textarea name="description" value={product.description} onChange={onChange} className="form-input" />
      </div>

      <div className="form-group">
        <label>Giá:</label>
        <input type="number" name="price" value={product.price} onChange={onChange} className="form-input" />
      </div>

      <div className="form-group">
        <label>Kho:</label>
        <input type="number" name="stock" value={product.stock} onChange={onChange} className="form-input" />
      </div>

      <div className="form-group">
        <label>Danh mục:</label>
        <select name="category_id" value={product.category_id} onChange={onChange} className="form-input">
          <option value="">-- Chọn danh mục --</option>
          {Array.isArray(categories) &&
            categories.map((cat) => (
              <option key={cat.category_id} value={cat.category_id}>
                {cat.cate_name}
              </option>
            ))}
        </select>
      </div>

      <div className="form-group">
        <label>Combo:</label>
        <select name="category_id_combo" value={product.category_id_combo} onChange={onChange} className="form-input">
          <option value="">-- Chọn combo --</option>
          <option value={3}>Combo 1 người</option>
          <option value={4}>Combo nhóm</option>
        </select>
      </div>

      <div className="form-group">
        <label>Ưu đãi:</label>
        <select
          name="category_id_uu_dai"
          value={product.category_id_uu_dai ?? ""}
          onChange={onChange}
          className="form-input"
        >
          <option value="">Không</option>
          <option value="1">Có</option>
        </select>
      </div>

      <div className="form-group">
        <label>Ảnh:</label>
        <input type="text" name="url_img" value={product.url_img} onChange={onChange} className="form-input" />
      </div>
    </div>
  );
};

export default ProductForm;
