// src/components/OrderTable/OrderTable.js
import React from "react";

const OrderTable = ({ orders, onEdit, onViewDetails, onDelete }) => {
  return (
    <table className="table-responsive-yeah">
      <thead>
        <tr>
          <th>Tên khách hàng</th>
          <th>Trạng thái</th>
          <th>Giá</th>
          <th>Ngày</th>
          <th>Hành động</th>
        </tr>
      </thead>
      <tbody>
        {orders.map((order) => (
          <tr key={order.orderId}>
            <td>{order.fullName}</td>
            <td>{order.status}</td>
            <td>{order.totalPrice.toLocaleString("vi-VN")}₫</td>
            <td>{order.orderTime}</td>
            <td>
              <button onClick={() => onEdit(order)}>Sửa</button>
              <button onClick={() => onViewDetails(order)}>Chi tiết</button>
              <button
                onClick={() => {
                  if (window.confirm("Bạn có chắc chắn muốn xoá đơn hàng này?")) {
                    onDelete(order.orderId);
                  }
                }}
                style={{ color: "red", marginLeft: "5px" }}
              >
                Xóa
              </button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default OrderTable;
