import React, { useState, useEffect } from 'react';
import Navbar from '../../components/Navbar/Navbar';
import Header from '../../components/Header/Header';
import ProductFormModal from '../../components/ProductFormModal/ProductFormModal';
import ProductTableNew from '../../components/ProductTableNew/ProductTableNew';

import {
  getAllProducts,
  createProduct,
  updateProduct,
  deleteProductById,
} from '../../services/prodmanaService';

import '../../assets/prodmana.css';

const ProductManagement = () => {
  const [products, setProducts] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editingProduct, setEditingProduct] = useState(null);

  // Tải dữ liệu sản phẩm từ backend
  const fetchProducts = async () => {
    try {
      const data = await getAllProducts();
      setProducts(data);
    } catch (err) {
      console.error('Lỗi khi tải sản phẩm:', err);
    }
  };

  // Tải lần đầu khi component mount
  useEffect(() => {
    fetchProducts();
  }, []);

  // Xử lý khi nhấn nút "Thêm sản phẩm"
  const handleAddClick = () => {
    setEditingProduct(null);
    setShowModal(true);
  };

  // Xử lý thêm / sửa sản phẩm
  const handleSubmit = async (productData) => {
    try {
      if (editingProduct) {
        await updateProduct(editingProduct.product_id, productData);
      } else {
        await createProduct(productData);
      }
      setShowModal(false);
      fetchProducts(); // reload sau khi thêm/sửa
    } catch (err) {
      alert('Lỗi khi gửi dữ liệu');
      console.error(err);
    }
  };

  // Xử lý xoá sản phẩm
  const handleDelete = async (productId) => {
    if (window.confirm('Bạn có chắc chắn muốn xoá sản phẩm này?')) {
      try {
        await deleteProductById(productId);
        fetchProducts(); // reload sau khi xoá
      } catch (err) {
        alert('Lỗi khi xoá sản phẩm');
        console.error(err);
      }
    }
  };

  return (
    <div className="admin-page">
      <Navbar />
      <div className="main-content">
        <Header />
        <main>
          <h1>Quản lý sản phẩm</h1>
          <button onClick={handleAddClick} className="add-button">Thêm sản phẩm</button>

          {/* Hiển thị danh sách sản phẩm */}
          <ProductTableNew
            products={products}
            onEdit={(product) => {
              setEditingProduct(product);
              setShowModal(true);
            }}
            onDelete={handleDelete}
          />
        </main>
      </div>

      {/* Modal thêm/sửa sản phẩm */}
      <ProductFormModal
        show={showModal}
        onClose={() => setShowModal(false)}
        onSubmit={handleSubmit}
        editingProduct={editingProduct}
        onDeleted={fetchProducts} // gọi lại sau khi xoá trong modal (nếu có)
      />
    </div>
  );
};

export default ProductManagement;
