import React, { useEffect } from 'react';
import 'morris.js/morris.css';
import Morris from 'morris.js/morris';
import Raphael from 'raphael';

window.Raphael = Raphael; // Morris.js cần Raphael ở global scope

const SalesChart = () => {
  useEffect(() => {
    new Morris.Bar({
      element: 'sales-chart',
      data: [
        { year: '2021', value: 20 },
        { year: '2022', value: 10 },
        { year: '2023', value: 25 },
      ],
      xkey: 'year',
      ykeys: ['value'],
      labels: ['Doanh số'],
      barColors: ['#0b62a4'],
    });
  }, []);

  return <div id="sales-chart" style={{ height: '250px' }} />;
};

export default SalesChart;
