// dashboardService.js

const API_BASE = "http://localhost:5000/api"; // địa chỉ backend API

// 1. Export dữ liệu
export const exportData = async () => {
  try {
    const response = await fetch(`${API_BASE}/financial-stats`, {
      method: 'GET',
      headers: { 'Content-Type': 'application/json' },
    });

    if (!response.ok) throw new Error("Failed to fetch financial stats");

    const data = await response.json();

    // Giả sử dữ liệu trả về như sau:
    // {
    //   purchases: { value: 17663, change: "-2%" },
    //   cancellations: { value: 4523.11, change: "<10%" },
    //   customers: { value: 46085, comment: "Ít hơn 2% số lượng truy cập" }
    // }

    const csvHeader = "Hạng mục,Giá trị,Thay đổi/Chú thích\n";
    const csvRows = [
      `Số lượng mua hàng,${data.purchases.value},${data.purchases.change}`,
      `Giá trị đơn hàng bị huỷ,$${data.cancellations.value},${data.cancellations.change}`,
      `Số lượng khách hàng đặt hàng,${data.customers.value},${data.customers.comment}`
    ];

    const csvContent = csvHeader + csvRows.join("\n");

    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const url = window.URL.createObjectURL(blob);

    const link = document.createElement('a');
    link.href = url;
    link.download = 'financial_stats.csv';
    link.click();
  } catch (error) {
    console.error("Export error:", error);
  }
};


// 2. Lấy tất cả sản phẩm
export const getAllProducts = async () => {
  try {
    const response = await fetch(`${API_BASE}/products`);
    if (!response.ok) throw new Error("Failed to fetch products");
    return await response.json();
  } catch (error) {
    console.error("Fetch products error:", error);
    return [];
  }
};

// 3. Lấy chi tiết một sản phẩm theo ID
export const getProductById = async (id) => {
  try {
    const response = await fetch(`${API_BASE}/products/${id}`);
    if (!response.ok) throw new Error("Failed to fetch product");
    return await response.json();
  } catch (error) {
    console.error("Fetch product by ID error:", error);
    return null;
  }
};
