import React, { useState, useEffect } from 'react';
import './ProductFormModel.css';

const ProductFormModal = ({ show, onClose, onSubmit, editingProduct }) => {
  const [product, setProduct] = useState({
    name: '',
    price: '',
    extraCosts: '',
    profit: '',
    type: '',
    description: '',
    status: '',
  });

  useEffect(() => {
    if (editingProduct) {
      setProduct(editingProduct);
    } else {
      setProduct({
        name: '',
        price: '',
        extraCosts: '',
        profit: '',
        type: '',
        description: '',
        status: '',
      });
    }
  }, [editingProduct]);

  if (!show) return null;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProduct((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(product);
    onClose();
  };

  return (
    <div className="modal-backdrop" onClick={onClose}>
      <div className="modal" onClick={(e) => e.stopPropagation()}>
        <h2>{editingProduct ? 'Chỉnh sửa sản phẩm' : 'Thêm sản phẩm'}</h2>
        <form onSubmit={handleSubmit}>
          <input name="name" placeholder="Tên sản phẩm" value={product.name} onChange={handleChange} />
          <input name="price" placeholder="Giá bán" value={product.price} onChange={handleChange} />
          <input name="extraCosts" placeholder="Chi phí khác" value={product.extraCosts} onChange={handleChange} />
          <input name="profit" placeholder="Lợi nhuận" value={product.profit} onChange={handleChange} />
          <input name="type" placeholder="Loại" value={product.type} onChange={handleChange} />
          <input name="description" placeholder="Mô tả" value={product.description} onChange={handleChange} />
          <input name="status" placeholder="Trạng thái" value={product.status} onChange={handleChange} />
          <div className="modal-buttons">
            <button type="submit">Lưu</button>
            <button type="button" onClick={onClose}>Hủy</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProductFormModal;
