import React, { useState, useEffect } from 'react';
import Navbar from '../../components/Navbar/Navbar';
import Header from '../../components/Header/Header';
import ProductFormModal from '../../components/ProductFormModel/ProductFormModel';
import {
  getAllProducts,
  createProduct,
  updateProduct,
  deleteProductById,
} from '../../services/prodmanaService';

import '../../assets/prodmana.css';

const ProductManagement = () => {
  const [products, setProducts] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editingProduct, setEditingProduct] = useState(null);

  const fetchProducts = async () => {
    try {
      const data = await getAllProducts();
      setProducts(data);
    } catch (err) {
      console.error('Lỗi khi tải sản phẩm:', err);
    }
  };

  useEffect(() => {
  axios.get('http://localhost:5000/api/products')
    .then((response) => {
      setProducts(response.data);
    })
    .catch((error) => {
      console.error('Lỗi khi tải sản phẩm:', error);
    });
}, []);

  const handleAddClick = () => {
    setEditingProduct(null);
    setShowModal(true);
  };

  const handleSubmit = async (productData) => {
    try {
      if (editingProduct) {
        await updateProduct(editingProduct.product_id, productData);
      } else {
        await createProduct(productData);
      }
      setShowModal(false);
      fetchProducts();
    } catch (err) {
      alert('Lỗi khi gửi dữ liệu');
    }
  };

  const handleDelete = async (productId) => {
    if (window.confirm('Bạn có chắc chắn muốn xoá sản phẩm này?')) {
      try {
        await deleteProductById(productId);
        fetchProducts();
      } catch (err) {
        alert('Lỗi khi xoá sản phẩm');
      }
    }
  };

  return (
    <div className="admin-page">
      <Navbar />
      <div className="main-content">
        <Header />
        <main>
          <h1>Quản lý sản phẩm</h1>
          <button onClick={handleAddClick}>Thêm sản phẩm</button>

          <table border="1" style={{ marginTop: '1rem', width: '100%' }}>
            <thead>
              <tr>
                <th>ID</th>
                <th>Tên</th>
                <th>Mô tả</th>
                <th>Giá</th>
                <th>Danh mục</th>
                <th>Kho</th>
                <th>Đã bán</th>
                <th>Hình ảnh</th>
                <th>Hành động</th>
              </tr>
            </thead>
            <tbody>
              {products.map((p) => (
                <tr key={p.product_id}>
                  <td>{p.product_id}</td>
                  <td>{p.name}</td>
                  <td>{p.description}</td>
                  <td>{p.price}</td>
                  <td>{p.category?.name || 'Không rõ'}</td>
                  <td>{p.stock}</td>
                  <td>{p.sold_quantity}</td>
                  <td>
                    <img src={p.url_img} alt="Ảnh sản phẩm" width="50" />
                  </td>
                  <td>
                    <button
                      onClick={() => {
                        setEditingProduct(p);
                        setShowModal(true);
                      }}
                    >
                      Sửa
                    </button>
                    <button onClick={() => handleDelete(p.product_id)}>Xoá</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </main>
      </div>

      <ProductFormModal
        show={showModal}
        onClose={() => setShowModal(false)}
        onSubmit={handleSubmit}
        editingProduct={editingProduct}
        onDeleted={fetchProducts}
      />
    </div>
  );
};

export default ProductManagement;
