import React, { useState } from "react";

const ProductTableNew = ({ products, categories, onEdit, onDelete }) => {
  const [editingProductId, setEditingProductId] = useState(null);
  const [editingName, setEditingName] = useState("");
  const [editingPrice, setEditingPrice] = useState(0);
  const [editingStock, setEditingStock] = useState(0);
  const [editingCategoryId, setEditingCategoryId] = useState("");

  const startEditing = (product) => {
    setEditingProductId(product.product_id);
    setEditingName(product.name);
    setEditingPrice(product.price);
    setEditingStock(product.stock);
    setEditingCategoryId(product.category_id || "");
  };

  const cancelEditing = () => {
    setEditingProductId(null);
    setEditingName("");
    setEditingPrice(0);
    setEditingStock(0);
    setEditingCategoryId("");
  };

  const saveEditing = (product) => {
    // Gọi onEdit với toàn bộ dữ liệu đã chỉnh sửa
    onEdit({
      ...product,
      name: editingName,
      price: Number(editingPrice),
      stock: Number(editingStock),
      category_id: Number(editingCategoryId),
    });
    cancelEditing();
  };

  return (
    <div>
      <h2 className="text-xl font-semibold mt-6">Danh sách sản phẩm</h2>
      <table>
        <thead>
          <tr>
            <th>Tên</th>
            <th>Giá</th>
            <th>Kho</th>
            <th>Danh mục</th>
            <th>Hành động</th>
          </tr>
        </thead>
        <tbody>
          {products.map((p) => (
            <tr key={p.product_id}>
              <td>
                {editingProductId === p.product_id ? (
                  <input
                    value={editingName}
                    onChange={(e) => setEditingName(e.target.value)}
                  />
                ) : (
                  p.name
                )}
              </td>
              <td>
                {editingProductId === p.product_id ? (
                  <input
                    type="number"
                    value={editingPrice}
                    onChange={(e) => setEditingPrice(e.target.value)}
                  />
                ) : (
                  p.price
                )}
              </td>
              <td>
                {editingProductId === p.product_id ? (
                  <input
                    type="number"
                    value={editingStock}
                    onChange={(e) => setEditingStock(e.target.value)}
                  />
                ) : (
                  p.stock
                )}
              </td>
              <td>
                {editingProductId === p.product_id ? (
                  <select
                    value={editingCategoryId}
                    onChange={(e) => setEditingCategoryId(e.target.value)}
                  >
                    <option value="">Chọn danh mục</option>
                    {categories.map((cat) => (
                      <option key={cat.category_id} value={cat.category_id}>
                        {cat.name}
                      </option>
                    ))}
                  </select>
                ) : (
                  categories.find((c) => c.category_id === p.category_id)?.name || ""
                )}
              </td>
              <td>
                {editingProductId === p.product_id ? (
                  <>
                    <button onClick={() => saveEditing(p)}>Lưu</button>
                    <button onClick={cancelEditing}>Hủy</button>
                  </>
                ) : (
                  <>
                    <button onClick={() => startEditing(p)}>Sửa</button>
                    <button onClick={() => onDelete(p.product_id)}>Xoá</button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ProductTableNew;
